long long int gcd ( int a , int b ) { int temp ; while ( a ) { temp = a ; a = b % a ; b = temp ; } return b ; } int main ( int argc , char const * argv [ ] ) { int t , a , b ; long long int hcf , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; if ( a > b ) lcm = gcd ( b , a ) ; else lcm = gcd ( a , b ) ; hcf = ( a * b ) / lcm ; printf ( "%lld %lld\n" , lcm , hcf ) ; } return 0 ; }