int main ( ) { long long int t , i ; scanf ( "%d" , & t ) ; while ( t > 0 ) { long long int n , i , j , c = 0 ; scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } c = n ; for ( i = 0 ; i < n ; i ++ ) { for ( j = i + 1 ; j < n ; j ++ ) { if ( a [ j ] > a [ j - 1 ] ) { c ++ ; } else break ; } } printf ( "count is %lld" , c ) ; } return 0 ; }