int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int numerator , denominator , n1 , n2 , remainder ; scanf ( "%ld%ld" , & n1 , & n2 ) ; if ( n1 > n2 ) { numerator = n1 ; denominator = n2 ; } else { numerator = n2 ; denominator = n1 ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } long int gcd = denominator ; long int lcm = n1 * n2 / gcd ; printf ( "%ld %ld\n" , gcd , lcm ) ; } return 0 ; }