long long int max ( long long int a , long long int b ) ; long long int hcf ( long long int a , long long int b ) ; int main ( ) { int n ; long long int a , b , lcm , gcd ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%lld%lld" , & a , & b ) ; if ( a == b ) printf ( "1 %lld\n" , a ) ; else { gcd = hcf ( a , b ) ; a = a / gcd ; b = b / gcd ; lcm = a * b * gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } } return 0 ; } long long max ( long long int a , long long int b ) { if ( a > b ) return a ; else return b ; } long long int hcf ( long long int a , long long int b ) { while ( a != b ) { if ( a > b ) { a = a - b ; } else { b = b - a ; } } return a ; }