int main ( void ) { int t , g , l , a , b , i , j , p , c , d ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { g = 1 ; scanf ( "%d %d" , & a , & b ) ; c = a ; d = b ; if ( a > b ) { p = b ; } else { p = a ; } for ( j = p ; j >= 1 ; j -- ) { if ( a % j == 0 ) { if ( b % j == 0 ) { g = g * j ; a /= j ; b /= j ; } } } l = ( c * d ) / g ; printf ( "%d %d\n" , g , l ) ; } return 0 ; }