int main ( ) { int T , i ; long int A , B , h , j ; long long int l , p ; scanf ( "%d" , & T ) ; for ( i = 1 ; i <= T ; i ++ ) { scanf ( "%ld%ld" , & A , & B ) ; if ( A > B ) { for ( j = 1 ; j <= B ; j ++ ) if ( A % j == 0 && B % j == 0 ) h = j ; } else { for ( j = 1 ; j <= A ; j ++ ) if ( A % j == 0 && B % j == 0 ) h = j ; } p = A * B ; l = p / h ; printf ( "%ld %lld" , h , l ) ; } return 0 ; }