long long int gcd ( long long int a , long long int b ) { if ( a == 0 || b == 0 ) { return 0 ; } if ( a == b ) { return a ; } if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } long long int lcm ( long long int a , long long int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( ) { int t ; long long int x , y , r , l ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld %lld" , & x , & y ) ; r = gcd ( x , y ) ; l = lcm ( x , y ) ; printf ( "%lld %lld" , r , l ) ; printf ( "\n" ) ; } return 0 ; }