int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n , i , c = 0 , sum = 0 ; scanf ( "%lld" , & n ) ; int a [ n ] , b [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] <= a [ i + 1 ] ) c ++ ; else { if ( c > 1 ) sum = sum + ( c * ( c - 1 ) / 2 ) ; else if ( c == 1 ) sum = sum + c ; c = 0 ; } } printf ( "%lld\n" , sum + n ) ; } }