int main ( void ) { int t , a , b , g , k = 0 , l ; scanf ( "%d" , & t ) ; while ( t > k ) { l = 0 ; g = 0 ; scanf ( "%d%d" , & a , & b ) ; g = gcd ( a , b ) ; l = lcm ( a , b ) ; printf ( "%d %d\n" , g , l ) ; k ++ ; } return 0 ; } int gcd ( int a , int b ) { if ( b == 0 ) return a ; return gcd ( b , a % b ) ; } int lcm ( int a , int b ) { int i , max ; if ( a < b ) max = b ; else max = a ; for ( i = max ; i <= ( a * b ) ; i ++ ) { if ( i % a == 0 && i % b == 0 ) return i ; } }