int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a ; scanf ( "%d" , & a ) ; int b [ a ] , i , l , j , k ; for ( i = 0 ; i < a ; i ++ ) { scanf ( "%d" , & b [ i ] ) ; } if ( b [ 0 ] > b [ 1 ] ) { k = b [ 1 ] ; j = 1 ; } else { k = b [ 0 ] ; j = 0 ; } for ( i = 1 ; i < a ; i ++ ) { if ( b [ i ] < k ) { k = b [ i ] ; } } l = ( a - 1 ) * k ; printf ( "%d\n" , l ) ; } return 0 ; }