int main ( ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { unsigned int num1 , num2 , gcd , lcm , remainder , numerator , denominator ; scanf ( "%u %u" , & num1 , & num2 ) ; if ( num1 > num2 ) { numerator = num1 ; denominator = num2 ; } else { numerator = num2 ; denominator = num1 ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd = denominator ; lcm = num1 * num2 / gcd ; printf ( "%u and %u\n" , gcd , lcm ) ; } return 0 ; }