int hcf ( int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; if ( a == b ) return a ; else if ( a > b ) return hcf ( a - b , b ) ; return hcf ( a , b - a ) ; } int main ( ) { int T , A , B ; scanf ( "%d" , & T ) ; for ( int i = 0 ; i < T ; i ++ ) { scanf ( "%d %d" , & A , & B ) ; int gcd = 0 , lcm = 0 ; gcd = hcf ( A , B ) ; lcm = ( A * B ) / gcd ; printf ( "%d %d \n" , gcd , lcm ) ; } }