int main ( ) { int i , j , k , sum , gr = 0 , row , t , n ; scanf ( "%d" , & t ) ; if ( t <= 1000 ) { for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & row ) ; gr = 0 ; if ( row <= 100 ) { for ( j = 1 ; j <= row ; j ++ ) { sum = 0 ; for ( k = 1 ; k <= j ; k ++ ) { scanf ( "%d" , & n ) ; if ( n > 0 && n < 100 ) { sum += n ; } } if ( sum > gr ) { gr = sum ; } } printf ( "%d\n" , gr ) ; } } } return 0 ; }