int main ( void ) { long int a , b , hcf , min , temp , max ; long int lcm ; int t , i , j ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld%ld" , & a , & b ) ; min = a >= b ? b : a ; max = a >= b ? a : b ; while ( 1 ) { if ( max % min == 0 ) { hcf = min ; break ; } else { temp = min ; min = max % min ; max = temp ; } } lcm = ( a * b ) / hcf ; printf ( "%ld" , hcf ) ; printf ( "%c%dld\n" , ' ' , lcm ) ; } return 0 ; }