int main ( ) { long long int a , b , max , min ; int i , n , j ; long long int p ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { min = b ; max = a ; } else { min = a ; max = b ; } for ( j = min ; j > 0 ; j = j - ( max - min ) ) { if ( a % j == 0 && b % j == 0 ) { p = ( a * b ) / j ; printf ( "%d %d\n" , j , p ) ; j = 0 ; } } } }