int main ( void ) { int i , j , t ; long int a , b , temp , gcd ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld %ld" , & a , & b ) ; if ( a > b ) { if ( a % b == 0 ) gcd = b ; else { for ( j = 1 ; j <= b / 2 ; j ++ ) { if ( a % j == 0 && b % j == 0 ) gcd = j ; } } } else { if ( b % a == 0 ) gcd = a ; else { for ( j = 1 ; j <= a / 2 ; j ++ ) { if ( a % j == 0 && b % j == 0 ) gcd = j ; } } } printf ( "%ld %ld\n" , gcd , a * b / gcd ) ; } }