int main ( ) { long int a [ 1000 ] , c , t , n ; scanf ( "%d" , & t ) ; while ( t -- ) { c = 0 ; long int i , j , k ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { for ( j = i + 1 ; j < n ; j ++ ) { if ( a [ i ] == ( a [ j ] + 1 ) ) { for ( k = i ; k < n ; k ++ ) a [ k ] = a [ k + 1 ] ; c = c + a [ j ] ; } else if ( a [ j ] == a [ i ] + 1 ) { for ( k = j ; k < n ; k ++ ) a [ k ] = a [ k + 1 ] ; c = c + a [ i ] ; } } } printf ( "%d\n" , c ) ; } return 0 ; }