int main ( ) { int a ; scanf ( "%d" , & a ) ; int i ; for ( i = 0 ; i < a ; i ++ ) { char s ; scanf ( "%c" , & s ) ; if ( i == 0 ) { scanf ( "%c" , & s ) ; } int count = 0 ; while ( s != '\n' ) { if ( s == 'A' || s == 'P' || s == 'D' || s == 'Q' || s == 'O' || s == 'R' ) { count ++ ; } else if ( s == 'B' ) { count = count + 2 ; } scanf ( "%c" , & s ) ; } printf ( "%d\n" , count ) ; } return 0 ; }