int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t > 0 ) { int n , i ; scanf ( "%d" , & n ) ; int a [ n ] , e , j , k , g ; for ( i = 0 ; i < n ; i ++ ) { a [ i ] = 0 ; } for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < i + 1 ; j ++ ) { scanf ( "%d" , & g ) ; for ( k = j ; k < n ; k = k + i + 1 ) { a [ k ] = a [ k ] + g ; } } } for ( i = 0 ; i < n ; i ++ ) { for ( j = 1 ; j < n - i ; j ++ ) { if ( a [ j ] < a [ j - 1 ] ) { e = a [ j ] ; a [ j ] = a [ j - 1 ] ; a [ j - 1 ] = e ; } } } printf ( "%d \n" , a [ n - 1 ] ) ; t = t - 1 ; } }