int main ( ) { int n , i , j , swap , a ; scanf ( "%lld" , & a ) ; while ( a -- ) { scanf ( "%lld" , & n ) ; int b [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & b [ i ] ) ; } for ( i = 0 ; i < n - 1 ; i ++ ) { for ( j = 0 ; j < n - 1 - i ; j ++ ) { if ( b [ j ] > b [ j + 1 ] ) { swap = b [ j ] ; b [ j ] = b [ j + 1 ] ; b [ j + 1 ] = swap ; } } } printf ( "%lld\n" , b [ 0 ] * ( n - 1 ) ) ; } return 0 ; }