int main ( ) { int t , i ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { long long int a , b , GCD = 0 , x , l , c = 0 , p , q ; scanf ( "%lld %lld" , & a , & b ) ; if ( a > b ) { p = a ; q = b ; c = a - b ; for ( l = 1 ; l <= c ; l ++ ) { c = b ; if ( a % b == 0 ) { GCD = b ; break ; } b = a % b ; a = c ; } printf ( "ll%d %lld\n" , GCD , ( p * q ) / GCD ) ; } else if ( b > a ) { p = a ; q = b ; c = b - a ; for ( l = 1 ; l <= c ; l ++ ) { c = a ; if ( b % a == 0 ) { GCD = a ; break ; } a = b % a ; b = c ; } printf ( "%lld %lld\n" , GCD , ( p * q ) / GCD ) ; } else if ( a == b ) { GCD = a ; printf ( "%lld %lld\n" , a , a ) ; } } return 0 ; }