int main ( ) { int t = 0 , c = 0 ; scanf ( "%d" , & t ) ; for ( c = 0 ; c < t ; c ++ ) { int x = 0 , y = 0 , a = 0 , b = 0 , prod = 0 , hcf = 1 , lcm = 1 , i = 0 , rem = 0 ; scanf ( "%d %d" , & x , & y ) ; a = MAX ( x , y ) ; b = MIN ( x , y ) ; prod = a * b ; while ( b != 0 ) { rem = a % b ; a = b ; hcf = a ; b = rem ; } lcm = prod / hcf ; printf ( "%d %d\n" , hcf , lcm ) ; } return 0 ; }