int main ( void ) { long long int tc , n , sum = 0 , a [ 100000 ] , i , first ; scanf ( "%lld" , & tc ) ; while ( tc > 0 ) { sum = 0 ; scanf ( "%lld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; first = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] < first ) { sum = sum + a [ i ] ; first = a [ i ] ; } else sum = sum + first ; } printf ( "%lld\n" , sum ) ; tc -- ; } return 0 ; }