int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; else if ( a == b ) return a ; else if ( a > b ) return gcd ( a - b , b ) ; else return gcd ( a , b - a ) ; } int main ( ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; int g = gcd ( a , b ) ; int l = ( a * b ) / g ; printf ( "%d %d" , g , l ) ; }