long int GCD ( long int , long int ) ; long int LCM ( long int , long int ) ; int main ( ) { int t ; scanf ( "%d" , & t ) ; long int a , b , c = 0 , d = 0 ; while ( t -- ) { scanf ( "%ld %ld" , & a , & b ) ; c = GCD ( a , b ) ; d = LCM ( a , b ) ; printf ( "%ld %ld\n" , c , d ) ; } return 0 ; } long int GCD ( long int a , long int b ) { if ( a % b == 0 ) return b ; else { if ( b % a == 0 ) return a ; else { if ( a > b ) return GCD ( b , a % b ) ; else return GCD ( a , b % a ) ; } } } long int LCM ( long int a , long int b ) { int x = 0 ; if ( a % b == 0 ) return a ; else { if ( b % a == 0 ) return b ; else { if ( a > b ) { x = GCD ( a , b ) ; if ( x == 1 ) return ( a * b ) ; else return ( x * b ) ; } else { x = GCD ( a , b ) ; if ( x == 1 ) return ( a * b ) ; else return ( x * a ) ; } } } }