int gcd ( int , int ) ; int lcm ( int , int ) ; int main ( ) { int i ; int g , l , T , A [ 1000 ] , B [ 1000 ] ; do { scanf ( "%d" , & T ) ; } while ( T < 1 || T > 1000 ) ; for ( i = 0 ; i < T ; i ++ ) { do { scanf ( "%d %d" , & A [ i ] , & B [ i ] ) ; } while ( A [ i ] < 1 || A [ i ] > 1000000 || B [ i ] < 1 || B [ i ] > 1000000 ) ; } printf ( "\n" ) ; for ( i = 0 ; i < T ; i ++ ) { g = gcd ( A [ i ] , B [ i ] ) ; l = lcm ( A [ i ] , B [ i ] ) ; printf ( "%d %d\n" , g , l ) ; } return 0 ; } int gcd ( int a , int b ) { if ( a == b ) return a ; else if ( a > b ) a = gcd ( a - b , b ) ; else a = gcd ( b - a , a ) ; return a ; } int lcm ( int a , int b ) { int temp , i ; if ( a > b ) { temp = b ; b = a ; a = temp ; } for ( i = 1 ; i <= b ; i ++ ) { if ( ( a * i ) % b == 0 ) return ( a * i ) ; } return 0 ; }