int gcd ( int m , int n ) { if ( m < n ) return gcd ( n , m ) ; if ( m % n == 0 ) return n ; else return gcd ( n , m % n ) ; } int lcm ( int m , int n ) { int i ; if ( n > m ) return lcm ( n , m ) ; if ( m % n == 0 ) return m ; for ( i = 2 ; i < n ; i ++ ) { if ( m % i == 0 && n % i == 0 ) { m /= i ; n /= i ; } } return ( m * n ) ; } int main ( ) { long a , b ; int t ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld %ld" , & a , & b ) ; printf ( "%ld %lld\n" , gcd ( a , b ) , lcm ( a , b ) ) ; } return 0 ; }