int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b ; scanf ( "%d%d" , & a , & b ) ; int x = a , y = b , r = 1 ; if ( x < y ) { while ( r != 0 ) { r = y % x ; y = x ; x = r ; } long int l = ( a * b ) / y ; printf ( "%d %ld\n" , y , l ) ; } else { while ( r != 0 ) { r = x % y ; x = y ; y = r ; } long int l = ( a * b ) / x ; printf ( "%d %ld\n" , x , l ) ; } } return 0 ; }