int gcd ( int a , int b ) { if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int lcm ( int a , int b ) { return ( ( a * b ) / gcd ( a , b ) ) ; } main ( ) { int t ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { int a , b ; scanf ( "%d%d" , & a , & b ) ; int m ; int n ; m = lcm ( a , b ) ; n = gcd ( a , b ) ; printf ( "%d %d" , n , m ) ; printf ( "\n" ) ; } }