int main ( ) { int t , a , b , lcm , gcd ; scanf ( "%i" , & t ) ; while ( t -- ) { scanf ( "%i%i" , & a , & b ) ; if ( a > b ) { a = a + b ; b = a - b ; a = a - b ; } lcm = b ; while ( lcm % a != 0 ) lcm += b ; if ( lcm = a * b ) { printf ( "%i %i\n" , 1 , lcm ) ; continue ; } gcd = a ; while ( b % gcd != 0 || a % gcd != 0 ) gcd -- ; printf ( "%i %i\n" , gcd , lcm ) ; } return 0 ; }