main ( ) { int t , n , i , j , k ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int a [ n ] [ n ] ; for ( i = 0 ; i < n ; i ++ ) for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & a [ i ] [ j ] ) ; for ( i = n - 2 ; i >= 0 ; i -- ) { j = i + 1 ; for ( k = 0 ; k <= j ; k ++ ) { if ( a [ j ] [ k ] > a [ j ] [ k + 1 ] ) a [ i ] [ k ] += a [ j ] [ k ] ; else a [ i ] [ k ] += a [ j ] [ k + 1 ] ; } } printf ( "%d\n" , a [ i + 1 ] [ i + 1 ] ) ; } }